cat exec_host|awk '{i=0;total = split($0,processornames,"+");{print total};for (p=1;p<=total;p++){print processornames[p];slash = index (processornames[p],"/");nodename = substr(processornames[p],1,slash-1);nodename_proc = substr(processornames[p],slash+1);print nodename,nodename_proc;if (match(nodename_proc,"-")){print nodename_proc;slash_proc = index(nodename_proc,"-");nodename_proc_begin = substr(nodename_proc,1,slash_proc-1); nodename_proc_end = substr(nodename_proc,slash_proc+1);nodename_proc_num = nodename_proc_end-nodename_proc_begin;i+=nodename_proc_num;print slash_proc,nodename_proc_begin,nodename_proc_end,nodename_proc_num,i}}}'
